import React from 'react';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

function NiceError(){
    const notify = () =>
    toast.error('   T   ', {
        position: "top-center",
        autoClose: 5000,
        hideProgressBar: false,
        closeOnClick: false,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        
        });
    return (
    <div>
        <button onClick={notify} className="errorBtn"
        >THERE IS AN ERROR! PLEASE CLICK HERE!</button>
        <ToastContainer
        className='toast'
        position="top-right"
        autoClose={5000}
        hideProgressBar={false}
        newestOnTop
        closeOnClick
        rtl
        pauseOnFocusLoss
        draggable
        pauseOnHover
        />
    </div>
    );
}
export default NiceError
