import React , {useContext,useState} from 'react';
import classes from './Cart.module.css';
import Modal from '../Ui/Modal';
import CartContext from './../../Store/CartContext';
import CartItem from './CartItem';
import CheckOut from './CheckOut';


const Cart = (props) => {

    const [isCheckout,setIsCheckout]=useState(false)
    const cartCtx =useContext(CartContext);
    const totalAmount = `$${cartCtx.totalAmount.toFixed(2)}`;
    const hasItems=cartCtx.items.length > 0;

    
    const cartItemRemoveHandler=id=>{
        cartCtx.removeItem(id)
    }
    
    const cartItemAddeHandler=item=>{
        cartCtx.addItem(item);
    }
    
    const handleOrder=()=>{
        setIsCheckout(true)
    }

    const submitOrderHandler=(userData)=>{
        fetch('https://meals-7c8f6-default-rtdb.firebaseio.com/meals/orders.json',{
                method:'POST',
                body: JSON.stringify({
                    user: userData,
                    orderedItems: cartCtx.items
                })
            });
    };

    const cartItems = (
        <ul className={classes['cart-items']}>
        {cartCtx.items.map((item) => (
            <CartItem
            key={item.id}
            name={item.name}
            amount={item.amount}
            price={item.price}
            onRemove={cartItemRemoveHandler.bind(null, item.id)}
            onAdd={cartItemAddeHandler.bind(null, item)}
            />
        ))}
        </ul>
    ); 

    const actions =             
        <div className={classes.actions}>
        <button className={classes['button--alt']} onClick={props.hideCart}>Close</button>
        {hasItems && <button className={classes.button} onClick={handleOrder}>Order</button>}
        </div>
    return (
        <Modal hideCart={props.hideCart}>
            {cartItems}
            <div className={classes.total}>
                <span>Total Amount</span>
                <span>{totalAmount}</span>
            </div>
            {isCheckout && 
            <CheckOut 
            onCancel={props.hideCart}
            onConfirm={submitOrderHandler}
            />}
            {!isCheckout && actions}
        </Modal>
    );
}

export default Cart;
